import React, { useCallback, useState } from "react";
import styles from "./signIn.module.css";
import { Link } from "react-router-dom";
import Modal from "../modal/modal.jsx";

const SignIn = () => {
  const modalEl = useRef();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [emailError, setEmailError] = useState(false);
  const [modalOpen, setModalOpen] = useState(false);
  const [bOpen, setOpen] = useState(false);

  const onSubmit = (e) => {
    e.preventDefault();
    console.log(email, password);
  };

  const onChangeEmail = useCallback((e) => {
    setEmailError(!isEmail(e.target.value));
    setEmail(e.target.value);
  }, []);

  const onChangePassword = useCallback((e) => {
    setPassword(e.target.value);
  }, []);

  const isEmail = (email) => {
    const regExp = /^[0-9a-zA-Z]([-_\.]?[0-9a-zA-Z])*@[0-9a-zA-Z]([-_\.]?[0-9a-zA-Z])*\.[a-zA-Z]{2,3}$/i;

    return regExp.test(email);
  };

  const openModal = () => {
    setModalOpen(true);
  };

  const closeModal = () => {
    setModalOpen(false);
  };

  const handleClickOutside = ({ target }) => {
    if (bOpen && !modalEl.current.contains(target)) setOpen(false);
  };

  useEffect(() => {
    window.addEventListener("click", handleClickOutside);
    return () => {
      window.removeEventListener("click", handleClickOutside);
    };
  }, []);

  return (
    <>
      <div className={styles.container}>
        <h2 className={styles.title}>로그인</h2>
        <form onSubmit={onSubmit} className={styles.form} action="index.html">
          <div>
            <div>
              <input
                className={styles.input}
                placeholder="이메일 주소"
                name="user-email"
                required
                onChange={onChangeEmail}
              />
              {emailError ? (
                <div className={styles.errorMessage}>
                  올바르지 않은 이메일 형식입니다.
                </div>
              ) : (
                <div className={styles.noError}>no error</div>
              )}
            </div>
          </div>
          <div>
            <input
              className={styles.input}
              type="password"
              placeholder="비밀번호"
              name="user-password"
              onChange={onChangePassword}
            />
            <div className={styles.noError}>no error</div>
          </div>
          <div id={styles.buttons}>
            <button
              id={styles.signUpButton}
              htmltype="submt"
              onSubmit={onSubmit}
            >
              로그인
            </button>
          </div>
        </form>
        <button onClick={openModal}>이메일 찾기</button>
        <Modal ref={modalEl} open={modalOpen} close={closeModal}></Modal>
        <div>
          스터디메이커가 처음이신가요? <Link to="/signup">회원가입</Link>
        </div>
      </div>
    </>
  );
};

export default SignIn;
