import React, { useCallback, useState } from "react";
import "./modal.css";

const Modal = (props) => {
  const { open, close, header, button } = props;
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");

  const onSubmit = (e) => {
    e.preventDefault();
    console.log(name, phone);
  };

  const onChangeEmail = useCallback((e) => {
    setEmailError(!isEmail(e.target.value));
    setName(e.target.name);
  }, []);

  const onChangePhoneNumber = useCallback((e) => {
    setPhone(e.target.value);
  }, []);

  return (
    <div className={open ? "openModal modal" : "modal"}>
      {open ? (
        <section>
          <header>{header}</header>
          <main>{props.children}</main>
          <form onSubmit={onSubmit} className={styles.form} action="index.html">
            <div>
              <div>
                <input
                  className={styles.input}
                  placeholder="이메일 주소"
                  name="user-name"
                  required
                  onChange={onChangeEmail}
                />
                {emailError ? (
                  <div className={styles.errorMessage}>
                    올바르지 않은 이메일 형식입니다.
                  </div>
                ) : (
                  <div className={styles.noError}>no error</div>
                )}
              </div>
            </div>
            <div>
              <input
                className={styles.input}
                type="phone"
                placeholder="비밀번호"
                name="user-phone"
                onChange={onChangePhoneNumber}
              />
              <div className={styles.noError}>no error</div>
            </div>
            <div id={styles.buttons}>
              <button
                id={styles.signUpButton}
                htmltype="submt"
                onSubmit={onSubmit}
              >
                로그인
              </button>
            </div>
          </form>
          <footer>
            <button className="close" onClick={close}>
              {button}
            </button>
            <button className="close" onClick={close}>
              닫기
            </button>
          </footer>
        </section>
      ) : null}
    </div>
  );
};

export default Modal;
