import React, { useState } from "react";

const SignUp = (props) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [passwordCheck, setPasswordCheck] = useState("");
  const [passwordError, setPasswordError] = useState(false);

  const onSubmit = useCallback(
    (e) => {
      e.preventDefault();
      if (password !== passwordCheck) {
        return setPasswordError(true);
      }
    },
    [password, passwordCheck]
  );

  const onChangePasswordChk = useCallback(
    (e) => {
      //비밀번호를 입력할때마다 password 를 검증하는 함수
      setPasswordError(e.target.value !== password);
      setPasswordCheck(e.target.value);
      //password state를 사용하기때문에 password를 넣어준다
    },
    [passwordCheck]
  );

  const useInput = (initValue = null) => {
    const [value, setter] = useState(initValue);
    const handler = useCallback((e) => {
      setter(e.target.value);
    }, []);
    return [value, handler];
  };

  return (
    <>
      <h2>회원가입</h2>
      <form className="form" action="index.html" method="">
        <div>
          <label>이메일</label>
          <input name="user-email" type="email" placeholder="이메일 주소" />
          <button type="submit">중복확인</button>
        </div>
        <div>
          <label>비밀번호</label>
          <input name="user-password" type="password" placeholder="비밀번호" />
        </div>
        <div>
          <label>비밀번호 확인</label>
          <input name="user-email" type="email" placeholder="비밀번호 확인" />
        </div>
        <div>
          <label>이름</label>
          <input name="user-name" type="text" placeholder="이름(본명)" />
        </div>
        <div>
          <label>닉네임</label>
          <input name="user-nickname" type="text" placeholder="닉네임(별명)" />
          <button type="submit">중복확인</button>
        </div>
        <div>
          <button>돌아가기</button>
          <button type="submit">가입하기</button>
        </div>
      </form>
    </>
  );
};

export default SignUp;
